			return false; 
	}

	main(Input Q)
	{
		Lefagy(Q)
	}
}

Program T1000
{

	boolean Lefagy(Program P)
	{
		 if(P-ben van végtelen ciklus)
			return true;
		 else
			return false; 
	}

	boolean Lefagy2(Program P)
	{
		 if(Lefagy(P))
			return true;
		 else
			for(;;); 
	}

	main(Input Q)
	{
		Lefagy2(Q)
	}

}

Program procheating
{
	#include <stdio.h>

        int main()
        {
        //change the values with an extra variable
        int a, b, c;
        a=2;
        b=5;
        c=0;

        //before the trade
        printf("csere elott:\na=%d, b=%d\n",a,b);
        c=a;
        a=b;
        b=c;
        //after the trade
        printf("csere utan:\na=%d, b=%d\n",a,b);

        //no extra variable used..
        //change values with exor
        a=3;
        b=8;

        //values before the trade
        printf("\ncsere exorral:\n");
        printf("csere elott:\na=%d, b=%d\n",a,b);

        a=a+b;
        b=a-b;
        a=a-b;

        printf("csere utan:\na=%d, b=%d\n",a,b);
        }

}

section.para:
Az if nélküli módszer 
Program bouncingball
{
#include <stdio.h>
#include <stdlib.h>
#include <curses.h>
#include <unistd.h>

int main(void)
{
    int xj = 0, xk = 0, yj = 0, yk = 0;
    int mx = 80 * 2, my = 24 * 2;

    WINDOW *ablak;
    ablak = initscr();
    noecho ();
    cbreak ();
    nodelay (ablak,true);

    for (;;)
    {
        xj = (xj - 1) % mx;
        xk = (xk + 1) % mx;

        yj = (yj - 1) % my;
        yk = (yk + 1) % my;

        clear();

        mvprintw(0, 0,
                 "---------------------------------------------------------------");
                 mvprintw(24, 0,
                 "---------------------------------------------------------------");
                 mvprintw(abs ((yj + (my - yk)) / 2),
                            abs ((xj + (mx - xk)) / 2), "X");
        refresh();
        usleep(150000);
    }
    return 0;
}

}
